jQuery(document).ready(function($){$("#sidebar").mCustomScrollbar({theme:"minimal"});$('.search-button svg').on('click',function(){$(this).parents('.search-button').toggleClass('open');$(this).parents('.search-button').find('.search-field').focus();});$('#dismiss, .overlay').on('click',function(){$('#sidebar').removeClass('active');$('.overlay').removeClass('active');$('html').removeClass('menu-open');$('body').removeClass('menu-open');});$('#sidebarCollapse').on('click',function(e){e.preventDefault();$('#sidebar').addClass('active');$('.overlay').addClass('active');$('html').addClass('menu-open');$('body').addClass('menu-open');$('.collapse.in').toggleClass('in');$('a[aria-expanded=true]').attr('aria-expanded','false');});$('.dropdown').on('show.bs.dropdown',function(){$(this).find('.dropdown-menu').first().stop(true,true).slideDown();});$('.dropdown').on('hide.bs.dropdown',function(){$(this).find('.dropdown-menu').first().stop(true,true).slideUp();});$('#sidebar a').on('click',function(){$('.overlay').click();});});;